	);

	// wrapped_key
	let sender_pub_key_buffer = from_base64_to_uint8array(sender_public_key)
	let aes_key = await derive_aes_key(sender_pub_key_buffer,local_private)
	return aes_key
}




//$>>	derive_key_jwk
/*
// derive_key_jwk
// Parameters:
//        -- sender_public_key :  as a JSON.parseable string representing jwk.
